TriggerOraclev10g.body:
DECLARE
  v_count NUMBER;
BEGIN
  SELECT COUNT(*) INTO v_count
  FROM Antropometria
  WHERE ID_Atleta = :NEW.ID_Atleta AND TRUNC(FechaRegistro) = TRUNC(:NEW.FechaRegistro);

  IF v_count > 0 THEN
    RAISE_APPLICATION_ERROR(-20001, 'Ya existe un registro de antropometrÃ­a para el mismo atleta en la misma fecha.');
  END IF;
END;
